#!/usr/bin/env bash

nkp create cluster nutanix -c $CLUSTER_NAME --endpoint https://$NUTANIX_ENDPOINT:$NUTANIX_PORT  --insecure  --control-plane-endpoint-ip $CONTROL_PLANE_ENDPOINT_IP  --kubernetes-service-load-balancer-ip-range $LB_IP_RANGE --control-plane-vm-image $NUTANIX_MACHINE_TEMPLATE_IMAGE_NAME --control-plane-prism-element-cluster $NUTANIX_PRISM_ELEMENT_CLUSTER_NAME --control-plane-subnets $NUTANIX_SUBNET_NAME  --worker-vm-image $NUTANIX_MACHINE_TEMPLATE_IMAGE_NAME --worker-prism-element-cluster $NUTANIX_PRISM_ELEMENT_CLUSTER_NAME --worker-subnets $NUTANIX_SUBNET_NAME --csi-storage-container $NUTANIX_STORAGE_CONTAINER_NAME --self-managed --registry-url https://docker.io --registry-username $REGISTRY_USER --registry-password $REGISTRY_PASSWORD --bootstrap-cluster-image docker.io/mesosphere/konvoy-bootstrap:$NKP_VERSION --timeout $TIMEOUT

